import React from 'react'
import { useState } from 'react'
const AddTask = ({onAdd}) => {
    const [text,setText] = useState('')
    const [day,setDay] = useState('')
    const [reminder,setReminder] = useState(false)
    const onSubmit = (e) =>{

        e.preventdefault()
        if(!text){
            alert('please add a task')
            return
        }
        onAdd({text,day,reminder})
        setText('')
        setDay('')
        setReminder(false)

    }
  return (
    <form onSubmit={onSubmit}>
    <div className='form-control' 
    >
    <label htmlFor='text'>Task</label>
    <input id='text' name='text' type="text" placeholder='Add Task' 
    value={text} 
    onChange={(e)=> setText(e.target.value
    )}
    />
    </div>
    <div className='form-control'>
    <label htmlFor='day'>Day and Time</label>
    <input id='day' name='day' type="text" placeholder='Day and time'
    value={day} 
    onChange={(e)=> setDay(e.target.value
    )}

    />
    </div>
    <div className='form-control 
    form-control-check'>
    <label htmlFor='reminder'>Set Reminder</label>
    <input 
    id='reminder'
    type="checkbox" 
    checked={reminder}
    value={reminder} 
    onChange={(e)=> setReminder(e.currentTarget.checked
    )}

    />
    </div>
    <input name='reminder' type='submit' value='Save Task' className='btn btn-block'/>
    </form>
  )
}

export default AddTask
